package controller;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;@Controller@RequestMapping("transController")public class Dictionary {    public static String[] foods = {"Steak","Noodle","Salmon"};    public static String[] trans = {"Thịt bò","Mỳ","Cá hồi"};    @RequestMapping("trans")    public String sayHello(){        return "dictionary";    }    @GetMapping("translate")    public String translate(Model model, @RequestParam String text){        for (int i = 0; i < foods.length; i++) {             if (text.equals(foods[i])){                 String translate = trans[i];                 model.addAttribute("textOut",translate);                 break;             }  else {                 String notify = "Không có kết quả !";                 model.addAttribute("textOut",notify);             }        }        return "dictionary";    }    }