package controller;import org.springframework.stereotype.Controller;import org.springframework.ui.Model;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestParam;@Controller@RequestMapping("changeMoney")public class MoneyChange {    @RequestMapping("hello")    public String hello() {        return "changeMoney";    }    @GetMapping("hello1")    public String hello(Model model) {        String notify = "Nhập vào số tiền bạn muốn chuyển đổi";        model.addAttribute("name", notify);        return "changeMoney";    }    @GetMapping("trans")    public String transUsd(Model mode, @RequestParam(defaultValue = "0") Integer usd, @RequestParam(defaultValue = "0") Integer vnd) {        if (usd == 0) {            Integer trans = vnd / 23000;            mode.addAttribute("usd", trans);        } else if (vnd == 0) {            Integer trans = usd * 23000;            mode.addAttribute("vnd", trans);        }        return "changeMoney";    }}